<?php define("__CONFIG__", true); require_once "../inc/config.php"; Test::TaskTableExists(); ?> <!DOCTYPE html> <html lang="en"> <head> <?php include_once "../inc/header.php"; ?> <title>Hello</title> </head> <body><form class="addtolist"> <label for="listItem"><h3>Enter the next task</h3></label> <input type="text" id="listItem" placeholder="I need to do...." required="required"> <button type="submit">Enter project</button> <div class="success" style="display:none;"></div> <div class="error" style="display:none"></div> </form> <table> <tr> <th>Task priority</th> <th>Task</th> <th>Task completion</th> </tr> <?php $con = DB::getConnection(); $projectName = "Hello"; $getTask = $con->prepare("SELECT id, task, done FROM tasks WHERE project = :project"); $getTask->bindParam(":project", $projectName, PDO::PARAM_STR); $getTask->execute(); $iteration = 1; while($data = $getTask->fetch()) : ?> <tr> <td><?php echo $iteration; $iteration++; ?></td> <td><?php echo $data["task"]; ?></td> <td><?php $id = $data["id"]; if($data["done"] == 0) : ?> <input type="checkbox" id="<?php echo $id; ?>"> <?php else : ?> <input type="checkbox" checked id="<?php echo $id; ?>"> <?php endif ?> </tr> <?php endwhile ?> </table> </body> <?php require_once "../inc/footer.php"; ?> </html> 